create table if not exists diseases
(
    id          bigint generated by default as identity
        constraint diseases_pkey
            primary key,
    name        varchar(255),
    description varchar(9999)

);

alter table diseases
    owner to postgres;


INSERT INTO diseases (name, description)
VALUES ('COVID-2019',
        'Коронавирусная инфекция COVID-19 воздействует на различные группы людей в разной степени. У большинства инфицированных болезнь протекает в легкой или средней форме тяжести, и им не требуется госпитализация.'),
       ('Аденовирусная инфекция',
        'Группа инфекционных заболеваний человека, возбудителем которых являются аденовирусы. Они относятся к группе острых респираторных вирусных инфекций (ОРВИ) и характеризуется поражением слизистых оболочек верхних дыхательных путей, конъюнктив, лимфоидной ткани. Имеет место лихорадка при умеренно выраженных симптомах интоксикации.'),
       ('Гастрофилёз (Gastrophilosis; Гастерофилёз)',
        'Доброкачественный миаз, вызываемый личинками желудочного овода.'),
       ('Конъюнктивит',
        'Воспаление слизистой оболочки глаза (конъюнктивы), вызванное аллергической реакцией или инфекцией (вирусной, реже бактериальной).
Различают аденовирусный (фарингоконъюнктивальная лихорадка), энтеровирусный, герпетический, бактериальный, хламидийный, аллергический, острый и хронический конъюнктивит. У взрослых 85 % случаев конъюнктивита вызваны аденовирусами и лишь 15 % — бактериями;
у детей бактериальный и аденовирусный конъюнктивиты встречаются с одинаковой частотой'),
       ('Гепатоз',
        'это группа заболеваний печени, в основе которых лежит нарушение обмена веществ в печёночных клетках (гепатоцитах) и развитие в клетках печени дистрофических изменений. При этом воспалительные явления отсутствуют или слабо выражены.'),
       ('Крапивница', 'Крапи́вница (лат. urticaria), называемая иногда крапивная лихорадка, крапивная сыпь, уртикария — кожное заболевание, дерматит преимущественно аллергического происхождения, характеризующееся быстрым появлением сильно зудящих, плоско приподнятых бледно-розовых волдырей (лат. urtica), сходных по виду с волдырями от ожога крапивой[3].
Согласно современному определению, крапивница — это группа заболеваний, характеризующаяся развитием зудящих волдырей и / или ангиоотёков[4][5].
Крапивница может быть самостоятельной (обычно аллергической) реакцией на какой-либо раздражитель либо является одним из проявлений какого-либо заболевания[3].'),
       ('Аортит',
        'Аортит (лат. aortitis от др.-греч. ἀορτή — «аорта») — воспаление стенки аорты инфекционной или аллергической (аутоимунной) природы. Наблюдается при сифилисе, сепсисе (особенно стрептококковом), туберкулёзе, ревматизме и др. Характеризуется расширением пораженного участка сосуда вплоть до образования аневризмы аорты. Нередки боли за грудиной (аорталгия), которые трудно отличить от стенокардии (приступы аорталгии обычно более длительны и не купируются нитроглицерином). Профилактика и лечение заключается в активной терапии основного заболевания; при образовании аневризмы аорты может потребоваться хирургическое лечение.'),
       ('Гемонхоз', 'Гемонхоз (Haemonchosis) — гельминтоз человека и животных из группы нематодозов.
Возбудитель — нематоды из рода Haemonchus. Распространено по всему миру. Гемонхусы достигают 18—35 мм длины. Личинки развиваются во внешней среде.
У жвачных животных вызывает тяжёлые расстройства всего организма, которые проявляются в поражении кишечника, нервной системы, кроветворных органов и эндокринных желёз; молодняк (а он наиболее восприимчив к инвазии) при этом часто гибнет. В южных районах у овец гемонхоз иногда протекает в виде энзоотий (чаще в годы с обильными осадками). Лечение проводят фенотиазином, который скармливают с дроблёным зерном.
У человека инвазия геогельминтом Haemonchus contortus встречается редко, болезнь протекает с диспепсическими явлениями. Заражение происходит через рот.'),
       ('Панникулит',
        'Панникулит (от лат. panniculus adiposus «жировые отложения», -itis «воспаление») — группа гетерогенных воспалительных заболеваний, характеризующихся поражением подкожной жировой клетчатки и нередко протекающих с вовлечением в процесс опорно-двигательного аппарата и внутренних органов.'),
       ('Игровое расстройство', 'Игровое расстройство (англ. gaming disorder) — модель игрового поведения, отличающаяся нарушением контроля за игрой[к. 1], отведением игре всё большего приоритета по сравнению с другими видами деятельности до такой степени, что ей отдается предпочтение перед другими интересами и повседневными занятиями, а также продолжением или интенсификацией игровой деятельности несмотря на появление нежелательных последствий[1].
Игровое расстройство является попыткой формализации зависимости от компьютерных игр со стороны Всемирной организации здравоохранения (ВОЗ)[2][3], которая в июне 2018 года включила игровое расстройство в 11-й перечень международной классификации болезней (МКБ-11), запланированный к вводу в действие с 1 января 2022 года[4]. Выделяются две основные формы расстройства: преимущественно онлайн и преимущественно оффлайн.'),
       ('Болезнь Пика',
           'Боле́знь Пи́ка — редкое, как правило, хроническое и прогрессирующее заболевание центральной нервной системы, встречающееся обычно в возрасте 50 — 60 лет и характеризующееся деструкцией и атрофией коры головного мозга преимущественно в области лобных и височных долей. Средний возраст начала заболевания — 54 года, средняя продолжительность до наступления смерти — 6 лет.'),
       ('Омфалит ', 'Омфалит (от др.-греч. ὀμφαλός — «пупок») — бактериальное воспаление дна пупочной ранки, пупочного кольца, подкожной жировой клетчатки вокруг пупочного кольца, пупочных сосудов
Заболевание начинается обычно в конце раннего неонатального периода или на 2-й неделе жизни, нередко с симптомов катарального омфалита. Через несколько дней появляется гнойное отделяемое из пупочной ранки — пиорея пупка, отёк и гиперемия пупочного кольца, инфильтрация подкожной жировой клетчатки вокруг пупка, вследствие которой пупок выбухает над поверхностью передней брюшной стенки. Кожа вокруг пупка гиперемирована, горячая на ощупь, характерно расширение сосудов передней брюшной стенки (усиление венозной сети), красные полосы, обусловленные присоединением лимфангоита. Появляются симптомы, характерные для инфекционного поражения пупочных сосудов. Состояние больного нарушено, ребёнок становится вялым, хуже сосёт грудь, срыгивает, уменьшается прибавка массы тела (возможна потеря). Температура тела повышена, иногда до фебрильной. В анализе крови лейкоцитоз со сдвигом влево, ускоренная СОЭ.
Прогноз при своевременно начатой комплексной терапии благоприятный, однако возможно возникновение метастатических очагов инфекции и генерализации процесса.'),
       ('Идиопатическая неспецифическая интерстициальная пневмония', 'Идиопатическая неспецифическая интерстициальная пневмония — одна из форм идиопатических интерстициальных пневмоний, характеризующаяся разнообразными гистологическими и компьютерно-томографическими признаками со схожей с ИЛФ (идиопатический легочный фиброз) клинической картиной, положительным эффектом от глюкокортикостероидов и более благоприятным прогнозом[1].
Впервые, как отдельную нозологию, данное заболевание описали в 1994 году Каценштейн и Фиорелли.'),
       ('Остеогенная саркома', 'Остеогенная саркома — саркома, злокачественные клетки которой происходят из костной ткани и продуцируют эту ткань. В некоторых из этих опухолей доминируют хондробластические или фибробластические компоненты. Рентгенологически подразделяется на остеолитическую, остеопластическую (склеротическую) и смешанную формы. Остеогенная саркома — чрезвычайно злокачественное новообразование. Возникая непосредственно из элементов кости, она характеризуется бурным течением и склонностью рано давать метастазы.
Термин «остеогенная саркома» был введён в 1920 г. Джеймсом Юингом')
        ,
       ('Нанофиетоз',
        'Нанофиетоз (лат. Nanophyetiasis) — гельминтоз из группы трематодозов, характеризующийся явлениями энтерита.'),
       ('Мицетома', 'Мицето́ма (эумикоти́ческая мицето́ма, мадуромико́з, маду́рская стопа́, англ. Maduro foot) — это хроническое, специфическое гранулематозное[1] заболевание грибковой природы[2].
Возбудителями мицетомы являются демациевые грибы (эумикотическая мицетома) или актиномицеты (актиномицетома) родов Actinomyces, Nocardia, Streptomyces, Actinomadura. Среди грибов встречаются: Madurella grisea, Phialophora cryanescens, Exophiala jeanselmei, Pseudallescheria boydii, Acremonium (Cephalosporium) falciforme, Leptosphaeria senegalensis, Curvularia spp.
Болезнь встречается в Индии, Африке, Центральной и Южной Америке[3]. Нозология относится к группе забытых (пренебрегаемых) болезней в связи с крайне низким распространением в развитых странах.')
        ,
       ('Дерматоз',
        'Дермато́з (с др.-греч. δέρμα означает «кожа») — это общее название различных заболеваний кожи и её придатков (волос, ногтей, желёз)[1]. Термином обозначают кожные поражения, которые характеризуются особыми проявлениями, с определенной этиологией (причиной) появления и патогенезом (механизмом развития). Могут быть как врождёнными, так и приобретёнными.');
